import { IStdReference } from "../interface/bandProtocol/IStdReference.sol";

contract TestStdReference is IStdReference {
    ReferenceData public refData;

    constructor() {}

    function setReferenceData(ReferenceData memory _refData) public {
        refData = _refData;
    }

    function getReferenceData(string memory _base, string memory _quote)
        external
        view
        override
        returns (ReferenceData memory)
    {
        return refData;
    }

    function getReferenceDataBulk(string[] memory _bases, string[] memory _quotes)
        external
        view
        override
        returns (ReferenceData[] memory)
    {
        revert();
    }
}

contracts/UniswapV3PriceFeed.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity >=0.7.6;
pragma experimental ABIEncoderV2;

import { Address } from "@openzeppelin/contracts/utils/Address.sol";
import { IUniswapV3Pool } from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import { FixedPoint96 } from "@uniswap/v3-core/contracts/libraries/FixedPoint96.sol";
import { PRBMath } from "prb-math/contracts/PRBMath.sol";
import { IPerpdexPriceFeed } from "./interface/IPerpdexPriceFeed.sol";

// TODO: support different token decimals like USDC
contract UniswapV3PriceFeed is IPerpdexPriceFeed {
    using Address for address;

    //
    // STATE
    //

    address public immutable pool;

    //
    // EXTERNAL NON-VIEW
    //

    constructor(address poolArg) {
        require(address(poolArg).isContract(), "UVPF_C: pool is not contract");

        pool = poolArg;
    }

    //
    // EXTERNAL VIEW
    //

    function getPrice() external view override returns (uint256) {
        (uint160 sqrtMarkPriceX96, , , , , , ) = IUniswapV3Pool(pool).slot0();
        uint256 markPriceX96 = _formatSqrtPriceX96ToPriceX96(sqrtMarkPriceX96);
        return _formatX96ToX10_18(markPriceX96);
    }

    //
    // EXTERNAL PURE
    //

    function decimals() external pure override returns (uint8) {
        return 18;
    }

    function _formatSqrtPriceX96ToPriceX96(uint160 sqrtPriceX96) internal pure returns (uint256) {
        return PRBMath.mulDiv(sqrtPriceX96, sqrtPriceX96, FixedPoint96.Q96);
    }

    function _formatX96ToX10_18(uint256 valueX96) internal pure returns (uint256) {
        return PRBMath.mulDiv(valueX96, 1e18, FixedPoint96.Q96);
    }
}

