    function fetchChainlinkPrice() external {
        currentPrice = IPerpdexPriceFeed(chainlink).getPrice();
    }

    function fetchBandProtocolPrice() external {
        currentPrice = IPerpdexPriceFeed(bandProtocol).getPrice();
    }
}
